mapper.insert:
insert into user(
               id, name, password,
               tel, mail,
               createtime,updatetime,
               state
               )
               values(
               #{id}, #{name}, #{password},
               #{tel}, #{mail},
               #{createtime},#{updatetime},
               #{state}
               );

mapper.update:
update user set id=#{id}, name=#{name},
                tel=#{tel}, mail=#{mail},
                createtime=#{createtime},
                updatetime=#{updatetime},
                state=#{state}
                where id=#{id};
